#!/usr/bin/env node
import yargs from "yargs/yargs";
import * as commands from "./commands/index.js";

yargs(process.argv.slice(2))
  .version()
  .count("verbose")
  .command(commands.compare)
  .command(commands.printToken)
  .command(commands.createToken)
  // .command(commands.deleteDatasets)
  // .command(commands.deleteQueries)
  // .command(commands.deleteGraphs)
  .command(commands.report)
  .command(commands.download)
  .alias("v", "verbose")
  .options({
    verbose: { type: "count", alias: "v" },
  })
  .demandCommand()
  .parseAsync()
  .then((_) => {})
  .catch((e) => console.error(e.message));
