import Destination, { Destination as DestinationType } from "./index.js";
import { ToTriplyDbOptions } from "../../middlewares/destinations/toTriplyDb.js";
const triplyDb = (args: ToTriplyDbOptions): DestinationType => {
  if (args.account) return Destination.TriplyDb.rdf(args.account, args.dataset, args.opts);
  else return Destination.TriplyDb.rdf(args.dataset, args.opts);
};

export default triplyDb;
