import { StaticString } from "../../../ratt/middlewares/assertions/term/str.js";
import { FileSourceInfo, SourceGetter } from "../../locations/sources/index.js";
import { QuerySourceGetter } from "../../locations/sources/savedQuery.js";
import fromCsv, { Options } from "./fromCsv.js";

// TODO @DocumentationTeam: add TS doc comment here
export default function fromTsv(
  oneOrMoreSources:
    | StaticString
    | SourceGetter<"records", FileSourceInfo>
    | SourceGetter<"records", FileSourceInfo>[]
    | QuerySourceGetter
    | QuerySourceGetter[],
  opts: Options = {},
) {
  return fromCsv(oneOrMoreSources, {
    delimiter: "\t",
    escape: false,
    quote: false,
    ...opts,
  });
}
