import { DefaultFactory } from "@triplydb/data-factory";
import { NamedNode, ValidationStatus } from "@triplydb/data-factory/Terms.js";
import { Iri as IriClass } from "@triplyetl/vocabularies";

// TODO @DocumentationTeam: add TS doc comment here
export default function declarePrefix<S extends string>(
  iri: S | NamedNode<S>,
  opts?: { validationStatus?: ValidationStatus; factory?: DefaultFactory },
): IriClass<S> {
  const iriString = typeof iri === "string" ? iri : iri.value;
  return new IriClass<S>(iriString, opts);
}
