import { isStatic, StaticString } from "../../../term/str.js";
import { LanguageTag } from "@triplyetl/vocabularies";

export const coerceToLanguageTag = (language: any | StaticString | LanguageTag): LanguageTag => {
  if (LanguageTag.isLanguageTag(language)) {
    return language;
  } else {
    if (isStatic(language) || typeof language === "string") {
      return new LanguageTag(language.toString());
    } else {
      throw new Error(`The value '${language}' (type '${typeof language}') does not hold a valid language tag.`);
    }
  }
};
