/**
 * This module holds the code for typing string primitives as a 'JSON Path'.
 */

export class JPath extends String {
  type = "JPath";

  constructor(value: any) {
    super(value);
  }
}

export function isJPath(value: any): value is JPath {
  return typeof value === "object" && Object.prototype.hasOwnProperty.call(value, "type") && value.type === "JPath";
}

// TODO @DocumentationTeam: add TS doc comment here
export default function jpath(value: any): JPath {
  return new JPath(value);
}
