import * as React from "react";
import { SerializedTraceInfo } from "../utils/Error.js";

export interface CliContext {
  verbosity: number;
  etlFile: string;
  useCmdInCliFeedback: boolean;
  offset?: number;
  token?: string;
  tokenApiUrl?: string;
  account?: string;
  dataDir: string;
  limit?: number;
  timeout?: number;
  debugTrace?: SerializedTraceInfo;
  errorTrace?: boolean;
  cmd: string[];
  logDir?: string;
  logId?: string;
  createErrorTrace?: boolean;
}
const CliContext = React.createContext<CliContext>({
  verbosity: 0,
  offset: undefined,
  useCmdInCliFeedback: true,
  cmd: [],
  etlFile: "",
  dataDir: "",
});
export function useCliContext() {
  return React.useContext(CliContext);
}

export default CliContext;
