import { Box, Text } from "ink";
import Spinner from "ink-spinner";
import { upperFirst } from "lodash-es";
import React from "react";
interface Props {
  type: "pre" | "post";
  executing: boolean;
}
const HookInfo: React.FC<Props> = ({ type, executing }) => {
  if (!executing) return null;
  return (
    <Box marginLeft={1} marginTop={1} alignItems="center">
      <Spinner type="dots" />
      <Box marginLeft={2}>
        <Text>{upperFirst(type)}processing</Text>
      </Box>
    </Box>
  );
};
export default HookInfo;
