export default function compareSemVer(expected: string, actual: string): 0 | 1 | -1 {
  const eAsNumber = parseInt(
    expected
      .replace(/^[^\d]+/, "")
      .split(".", 3)
      .map((i) => i.padStart(2, "0"))
      .join(""),
  );
  const aAsNumber = parseInt(
    actual
      .replace(/^[^\d]+/, "")
      .split(".", 3)
      .map((i) => i.padStart(2, "0"))
      .join(""),
  );
  return eAsNumber >= aAsNumber ? 1 : eAsNumber == aAsNumber ? 0 : -1;
}
