import { type Store } from '@triplydb/data-factory'
import Validator, { type ValidatorOptions } from './Validator.js'
import RulesEngine, { type RulesEngineOptions } from './RulesEngine.js'
import Writer from './Validator/Report/Writer.js'

// eslint-disable-next-line @typescript-eslint/no-extraneous-class
class Factory {
  public static validator = (model: Store, opts?: Partial<ValidatorOptions>): Validator => {
    return new Validator(model, opts)
  }

  public static rulesEngine = (model: Store, opts?: Partial<RulesEngineOptions>): RulesEngine => {
    return new RulesEngine(model, opts)
  }

  public static reportWriter = (validationReport: Store): Writer => {
    return new Writer(validationReport)
  }
}

export default Factory
