export default `
prefix sh: <http://www.w3.org/ns/shacl#> 
prefix def: <https://triplydb.com/model/def/>
prefix xsd: <http://www.w3.org/2001/XMLSchema#>

select ?sourceShape ?propertyShape ?focusNode ?resultPath ?anyURI ?resultSeverity where {
  ?sourceShape sh:property ?propertyShape .
  ?propertyShape sh:datatype xsd:anyURI; sh:path ?resultPath .
  ?focusNode ?resultPath ?anyURI .
  optional { ?propertyShape sh:severity ?propertySeverity}
  optional { ?sourceShape sh:severity ?shapeSeverity}
  bind(coalesce(?propertySeverity, ?shapeSeverity, sh:Violation) as ?resultSeverity)
  filter(datatype(?anyURI) = xsd:anyURI)
}
`
