export default `
prefix shacl: <http://www.w3.org/ns/shacl#>
prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#>

select ?shape ?rule ?ruleType where { 
  ?shape shacl:rule ?rule .
  filter not exists { ?shape shacl:decativated true }
  filter not exists { ?rule shacl:decativated true }

  optional { ?shape shacl:order ?orderByShape }
  bind(coalesce(?orderByShape, 0) as ?order1)

  optional { ?rule shacl:order ?orderByRule }
  bind(coalesce(?orderByRule, 0) as ?order2)

  ?rule a ?ruleType .
} order by ?order1 ?order2
`
