import ConstraintComponent from '../ConstraintComponent.js'

export default class ClosedConstraintComponent extends ConstraintComponent {
  public async toString (): Promise<string> {
    return await Promise.resolve(this.format('The node %o has a predicate %o%s; your shape %o is closed and does not allow this.', this.result.focusNode, this.result.resultPath ?? '[unknown sh:resultPath]', this.valuesToString(), this.result.sourceShape ?? '[unknown SHACL SourceShape]'))
  }
}
