import ConstraintComponent from '../ConstraintComponent.js'

export default class NodeConstraintComponent extends ConstraintComponent {
  public async toString (): Promise<string> {
    return await Promise.resolve(this.format('The node %o has a predicate %o%s that does not have shape %o.', this.result.focusNode, this.result.resultPath ?? '[unknown sh:resultPath]', this.valuesToString(), this.result.sourceShape ?? '[unknown SHACL SourceShape]'))
  }
}
