import { getFactory, type Terms } from '@triplydb/data-factory'

export default function declarePrefix<Prefix extends string> (base: Prefix | Terms.NamedNode<Prefix>): <Suffix extends string>(local: Suffix) => Terms.NamedNode<`${Prefix}${Suffix}`> {
  return function <Suffix extends string>(local: Suffix) {
    return getFactory().namedNode(`${typeof base === 'string' ? base : base.value}${local}`)
  }
}
