import * as prettier from 'prettier'
import path from 'path'

export async function pretty (content: string): Promise<string> {
  // return content.replace(/\t/g, ' ')
  const options = {
    parser: 'typescript',
    ...(await prettier.resolveConfig(path.join(process.cwd(), '.eslintrc.yml')))
  }
  return await prettier.format(content.replace(/\t/g, ' '), options)
}
