import type Job from '../Interfaces/Job.js'
import { writeFileSync } from 'fs'
import path from 'path'
export default (jobs: Job[]): void => {
  jobs.sort((a, b) => (a.prefix < b.prefix ? -1 : 1))
  writeFileSync(path.join(process.cwd(), 'vocabularies.json'), JSON.stringify(jobs, null, 2), 'utf-8')
}
