/**
 * Document Elements Ontology (DEO)
 * deo.ts (2023-08-08T17:30:08.916Z)
 */
import VocabularyBase from '../../Classes/Vocabulary.js'

class Vocabulary extends VocabularyBase<'http://purl.org/spar/deo/'> {
  /**
   * **[deo:acknowledgements](http://purl.org/spar/deo/Acknowledgements)**
   *
   * Usually part of the preface, or a separate section in its own right, often as part of the back matter, it acknowledges those, including funding agencies, who contributed to the undertaking of a research project described in a publication, or to the creation of the work in some way. In scientific articles, the acknowledgements are usually placed as a separated section immediately following the Discussion or Conclusions.
   *
   * @type        class
   * @parent      deo:DiscourseElement
   */
  public readonly Acknowledgements = this.toIri().concat('Acknowledgements')

  /**
   * **[deo:author contribution](http://purl.org/spar/deo/AuthorContribution)**
   *
   * A description of the roles played by an author in the publication.
   *
   * @type        class
   * @parent      deo:DiscourseElement
   */
  public readonly AuthorContribution = this.toIri().concat('AuthorContribution')

  /**
   * **[deo:background](http://purl.org/spar/deo/Background)**
   *
   * Presentation of information that is essential for understanding the situation or problem that is the subject of the publication. In a journal article, the background is usually part of the Introduction, but may be present as separated section.
   *
   * @type        class
   * @parent      deo:DiscourseElement
   */
  public readonly Background = this.toIri().concat('Background')

  /**
   * **[deo:bibliographic reference](http://purl.org/spar/deo/BibliographicReference)**
   *
   * A reference, usually contained in a footnote or a bibliographic reference list, that refer to another publication, such as a journal article, a book, a book chapter or a Web site. The inclusion of the bibliographic reference in a publication constitutes the performative act of bibliographic citation.
   *
   * @type        class
   * @parent      deo:Reference
   */
  public readonly BibliographicReference = this.toIri().concat('BibliographicReference')

  /**
   * **[deo:biography](http://purl.org/spar/deo/Biography)**
   *
   * Information describing a person and his or her life history and contributions.
   *
   * @type        class
   * @parent      deo:DiscourseElement
   */
  public readonly Biography = this.toIri().concat('Biography')

  /**
   * **[deo:caption](http://purl.org/spar/deo/Caption)**
   *
   * Text accompanying another item, such as a picture.
   *
   * @type        class
   * @parent      deo:DiscourseElement
   */
  public readonly Caption = this.toIri().concat('Caption')

  /**
   * **[deo:conclusion](http://purl.org/spar/deo/Conclusion)**
   *
   * A reflection on the preceding text, summarizing the evidence, arguments or premises presented in the document and their logical outcomes. Conclusions are a fundamental feature in academic research publications, and may be included in the Discussion section.
   *
   * @type        class
   * @parent      deo:DiscourseElement
   */
  public readonly Conclusion = this.toIri().concat('Conclusion')

  /**
   * **[deo:contribution](http://purl.org/spar/deo/Contribution)**
   *
   * A description of the part that this publication plays in the overall field.
   *
   * @type        class
   * @parent      deo:DiscourseElement
   */
  public readonly Contribution = this.toIri().concat('Contribution')

  /**
   * **[deo:data](http://purl.org/spar/deo/Data)**
   *
   * A textual description of data used or produced in the work which the document describes, or the data themselves.
   *
   * @type        class
   * @parent      deo:DiscourseElement
   */
  public readonly Data = this.toIri().concat('Data')

  /**
   * **[deo:dataset description](http://purl.org/spar/deo/DatasetDescription)**
   *
   * Information describing a dataset held in an external database or repository and including a reference to it, such as a database ID or an accession number.
   *
   * @type        class
   * @parent      deo:ExternalResourceDescription
   */
  public readonly DatasetDescription = this.toIri().concat('DatasetDescription')

  /**
   * **[deo:dedication](http://purl.org/spar/deo/Dedication)**
   *
   * Text in which the author names the person or people for whom he/she has written the document, or to whose memory it is dedicated.
   *
   * @type        class
   * @parent      deo:DiscourseElement
   */
  public readonly Dedication = this.toIri().concat('Dedication')

  /**
   * **[deo:discourse element](http://purl.org/spar/deo/DiscourseElement)**
   *
   * An element of a document that carries out a rhetorical function.
   *
   * @type        class
   * @parent      http://www.w3.org/2002/07/owl#Thing
   */
  public readonly DiscourseElement = this.toIri().concat('DiscourseElement')

  /**
   * **[deo:discussion](http://purl.org/spar/deo/Discussion)**
   *
   * An interpretation and discussion of the results obtained and an analysis of their significance, in support of conclusions. These conclusions may be part of this discussion or may be included in a separate section of the document.
   *
   * @type        class
   * @parent      deo:DiscourseElement
   */
  public readonly Discussion = this.toIri().concat('Discussion')

  /**
   * **[deo:epilogue](http://purl.org/spar/deo/Epilogue)**
   *
   * A piece of writing at the end of a work of literature or drama, usually used to bring closure to the work.
   *
   * @type        class
   * @parent      deo:DiscourseElement
   */
  public readonly Epilogue = this.toIri().concat('Epilogue')

  /**
   * **[deo:evaluation](http://purl.org/spar/deo/Evaluation)**
   *
   * A consideration of the value, meaning and significance of the results obtained.
   *
   * @type        class
   * @parent      deo:DiscourseElement
   */
  public readonly Evaluation = this.toIri().concat('Evaluation')

  /**
   * **[deo:external resource description](http://purl.org/spar/deo/ExternalResourceDescription)**
   *
   * Information describing an external resource and including a reference to that resource.
   *
   * @type        class
   * @parent      https://triplydb.com/.well-known/genid/10b2b57ccdb0f790ddca6543148f00dc
   */
  public readonly ExternalResourceDescription = this.toIri().concat('ExternalResourceDescription')

  /**
   * **[deo:future work](http://purl.org/spar/deo/FutureWork)**
   *
   * A proposal for new investigation to be undertaken in order to continue and advance the work described in the publication.
   *
   * @type        class
   * @parent      deo:DiscourseElement
   */
  public readonly FutureWork = this.toIri().concat('FutureWork')

  /**
   * **[deo:introduction](http://purl.org/spar/deo/Introduction)**
   *
   * An initial description which states the purpose and goals of the following writing, and, in the case of journal articles, typically includes background information on the research topic and a review of related work in the area.
   *
   * @type        class
   * @parent      deo:DiscourseElement
   */
  public readonly Introduction = this.toIri().concat('Introduction')

  /**
   * **[deo:legend](http://purl.org/spar/deo/Legend)**
   *
   * Informative text that explicitly explains another item, such as a figure or a table.
   *
   * @type        class
   * @parent      deo:Caption
   */
  public readonly Legend = this.toIri().concat('Legend')

  /**
   * **[deo:materials](http://purl.org/spar/deo/Materials)**
   *
   * A description in a research paper documenting the specialized materials used in the work described. This description is often combined with a description of the methods used, in a section entitled 'Methods and Materials', 'Experimental' or a related term.
   *
   * @type        class
   * @parent      deo:DiscourseElement
   */
  public readonly Materials = this.toIri().concat('Materials')

  /**
   * **[deo:methods](http://purl.org/spar/deo/Methods)**
   *
   * A description in a research paper documenting the specialized methods used in the work described. This description is often combined with a description of the materials used, in a section entitled 'Methods and Materials', 'Experimental' or a related term.
   *
   * @type        class
   * @parent      deo:DiscourseElement
   */
  public readonly Methods = this.toIri().concat('Methods')

  /**
   * **[deo:model](http://purl.org/spar/deo/Model)**
   *
   * A description of a model used or produced by the work described in the publication.
   *
   * @type        class
   * @parent      deo:DiscourseElement
   */
  public readonly Model = this.toIri().concat('Model')

  /**
   * **[deo:motivation](http://purl.org/spar/deo/Motivation)**
   *
   * A description of the justification for undertaking the work described in the publication.
   *
   * @type        class
   * @parent      deo:DiscourseElement
   */
  public readonly Motivation = this.toIri().concat('Motivation')

  /**
   * **[deo:postscript](http://purl.org/spar/deo/Postscript)**
   *
   * Text added after the signature of a letter, or sometimes after the main body of an essay or book.
   *
   * @type        class
   * @parent      deo:DiscourseElement
   */
  public readonly Postscript = this.toIri().concat('Postscript')

  /**
   * **[deo:problem statement](http://purl.org/spar/deo/ProblemStatement)**
   *
   * @type        class
   * @parent      deo:DiscourseElement
   */
  public readonly ProblemStatement = this.toIri().concat('ProblemStatement')

  /**
   * **[deo:prologue](http://purl.org/spar/deo/Prologue)**
   *
   * A piece of writing at the beginning of a work of literature or drama, usually used to set the scene or to introduce the work.
   *
   * @type        class
   * @parent      deo:DiscourseElement
   */
  public readonly Prologue = this.toIri().concat('Prologue')

  /**
   * **[deo:reference](http://purl.org/spar/deo/Reference)**
   *
   * A reference to a specific part of the document, or to another publication.
   *
   * @type        class
   * @parent      deo:DiscourseElement
   */
  public readonly Reference = this.toIri().concat('Reference')

  /**
   * **[deo:related work](http://purl.org/spar/deo/RelatedWork)**
   *
   * The authors' critical review of current knowledge by specific reference to others' work, both in terms of substantive findings and theoretical and methodological contributions to a particular topic. This description is often included within the introduction section.
   *
   * @type        class
   * @parent      deo:DiscourseElement
   */
  public readonly RelatedWork = this.toIri().concat('RelatedWork')

  /**
   * **[deo:results](http://purl.org/spar/deo/Results)**
   *
   * The report of the specific findings of an investigation, given without discussion or conclusion being drawn.
   *
   * @type        class
   * @parent      deo:DiscourseElement
   */
  public readonly Results = this.toIri().concat('Results')

  /**
   * **[deo:scenario](http://purl.org/spar/deo/Scenario)**
   *
   * A presentation of a use case or test, based on a real or hypothetical situation, used to help someone think through a complex problem or system.
   *
   * @type        class
   * @parent      deo:DiscourseElement
   */
  public readonly Scenario = this.toIri().concat('Scenario')

  /**
   * **[deo:supplementary information description](http://purl.org/spar/deo/SupplementaryInformationDescription)**
   *
   * Information describing supplementary information relating to the document, including references or links to the relevant supplementary information.
   *
   * @type        class
   * @parent      deo:ExternalResourceDescription
   */
  public readonly SupplementaryInformationDescription = this.toIri().concat('SupplementaryInformationDescription')
}

/**
 * prefix **deo:** <<http://purl.org/spar/deo/>>
 *
 * Document Elements Ontology (DEO)
 *
 * DEO, The Discourse Elements Ontology, is an ontology written in OWL 2 DL that provides a structured vocabulary for rhetorical elements within documents (e.g. Introduction, Discussion, Acknowledgements, Reference List, Figures, Appendix), enabling these to be described in RDF.  It uses some of the rhetorical block elements from the SALT Rhetorical Ontology and the Ontology of Rhetorical Blocks.
 *
 * Version: 2015-07-03
 */
const deo = new Vocabulary('http://purl.org/spar/deo/')
export default deo
