import { getFactory } from '@triplydb/data-factory'
import cc from '../cc.js'
import { assert } from 'chai'
import epsg from '../epsg.js'
import owms from '../owms.js'
import xsd from '../xsd.js'
describe('Test static vocabularies', () => {
  it('Should work with the CC vocabulary', () => {
    assert.isTrue(cc.by['1.0'].equals(getFactory().namedNode('https://creativecommons.org/licenses/by/1.0/')))
  })
  it('Should work with the EPSG vocabulary', () => {
    assert.isTrue(epsg[28992].equals(getFactory().namedNode('http://www.opengis.net/def/crs/EPSG/0/28992')))
  })
  it('Should work with the OWMS vocabulary', () => {
    assert.isTrue(owms.gemeente.Heiloo.equals(getFactory().namedNode('http://standaarden.overheid.nl/owms/terms/Heiloo_(gemeente)')))
  })
  it('Should work with the XSD vocabulary', () => {
    assert.isTrue(xsd.boolean.equals(getFactory().namedNode('http://www.w3.org/2001/XMLSchema#boolean')))
  })
})
