import VocabularyBase from '../../Classes/Vocabulary.js'

class Vocabulary extends VocabularyBase<'http://www.opengis.net/def/crs/EPSG/0/'> {
  /**
   * Amersfoort / RD New -- Netherlands - Holland - Dutch
   */
  28992 = this.concat('28992')
  /**
   * WGS 84 / Pseudo-Mercator -- Spherical Mercator, Google Maps, OpenStreetMap, Bing, ArcGIS, ESRI
   */
  3857 = this.concat('3857')
  /**
   * WGS 84 -- WGS84 - World Geodetic System 1984, used in GPS
   */
  4326 = this.concat('4326')

  // found these in TriplyETL ...
  666 = this.concat('666')
  666666 = this.concat('666666')
}

/**
 * prefix **epsg:** <<http://www.opengis.net/def/crs/EPSG/0/>>
 *
 * EPSG Geodetic Parameter Dataset (EPSG)
 */
const epsg = new Vocabulary('http://www.opengis.net/def/crs/EPSG/0/')
export default epsg
